#include <dtprec.h>#include <cppunit/tester.h>#include <dtl/dtl.h>#include "uinttc.h"CPPUNIT_TEST_SUITE_REGISTRATION( UIntTestCase );voidUIntTestCase::toString(void){	String str;	str = UInt::toString ( (unsigned int) 6 );	CPPUNIT_ASSERT ( str == String ("6") );	str = UInt::toString ( (unsigned int) 556 );	CPPUNIT_ASSERT ( str == String ("556") );	str = UInt::toString ( (unsigned int) -1 );	CPPUNIT_ASSERT ( str == String ("4294967295") );	str = UInt::toString ( (unsigned int) 0x16 );	CPPUNIT_ASSERT ( str == String ("22") );	str = UInt::toString ( (unsigned int) 016 );	CPPUNIT_ASSERT ( str == String ("14") );		str = UInt::toString ( (unsigned int) -016 );	CPPUNIT_ASSERT ( str == String ("4294967282") );	str = UInt::toString ( (unsigned int) 0776 );	CPPUNIT_ASSERT ( str == String ("510") );	str = UInt::toString ( (unsigned int) -0776 );	CPPUNIT_ASSERT ( str == String ("4294966786") );	str = UInt::toString ( (unsigned int) 0000 );	CPPUNIT_ASSERT ( str == String ("0") );	str = UInt::toString ( (unsigned int) -0000 );	CPPUNIT_ASSERT ( str == String ("0") );	str = UInt::toString ( (unsigned int) 0006 );	CPPUNIT_ASSERT ( str == String ("6") );	str = UInt::toString ( (unsigned int) -0006 );	CPPUNIT_ASSERT ( str == String ("4294967290") );	str = UInt::toString ( (unsigned int) 'a' );	CPPUNIT_ASSERT ( str == String ("97") );	str = UInt::toString ( (unsigned int) -'a' );	CPPUNIT_ASSERT ( str == String ("4294967199") );}